mapper.select:
select viewAppointment.id as viewAppointment_id, viewAppointment.user_id, viewAppointment.name as viewAppointment_name, viewAppointment.phone as viewAppointment_phone, viewAppointment.apartment_id, viewAppointment.appointment_time, viewAppointment.additional_info, viewAppointment.appointment_status, apartmentInfo.id as apartmentInfo_id, apartmentInfo.name as apartmentInfo_name, apartmentInfo.introduction, apartmentInfo.district_id, apartmentInfo.district_name, apartmentInfo.city_id, apartmentInfo.city_name, apartmentInfo.province_id, apartmentInfo.province_name, apartmentInfo.address_detail, apartmentInfo.latitude, apartmentInfo.longitude, apartmentInfo.phone as apartmentInfo_phone, apartmentInfo.is_release, from view_appointment as viewAppointment left join apartment_info as apartmentInfo on viewAppointment.apartment_id = apartmentInfo.id and apartmentInfo.is_deleted = 0
viewAppointment.is_deleted = 0
and apartmentInfo.province_id = #{queryVo.provinceId}
and apartmentInfo.city_id = #{queryVo.cityId}
and apartmentInfo.district_id = #{queryVo.districtId}
and viewAppointment.apartment_id = #{queryVo.apartmentId}
and viewAppointment.name like concat('%', #{queryVo.name}, '%')
and viewAppointment.phone like concat('%', #{queryVo.phone}, '%')
